/* eslint-disable import/no-cycle */
import { useMutation, useQuery } from '@apollo/client';
import gql from 'graphql-tag';
import Router from 'next/router';
import useForm from '../lib/useForm';
import Form from './styles/Form';
import { UPDATE_PRODUCT_MUTATION } from './Products/UpdateProduct';

export default function EditForm({
  product: { name, description, price, photo },
}) {
  const { inputs, handleInputChange, clearForm, resetForm } = useForm({
    name: '',
    description: '',
    price: 0,
    image: '',
  });

  const [createProduct, { data, loading, error }] = useMutation(
    UPDATE_PRODUCT_MUTATION,
    {
      variables: inputs,
      refetchQueries: [{ query: PRODUCT_QUERY }],
    }
  );

  return (
    <Form
      onSubmit={async (e) => {
        e.preventDefault();
        console.log(data);
      }}
    >
      <fieldset aria-busy={loading} disabled={loading}>
        <label htmlFor="image">
          Image
          <input
            required
            id="image"
            name="image"
            type="file"
            onChange={(e) => handleInputChange(e)}
          />
        </label>
        <label htmlFor="name">
          Name
          <input
            id="name"
            name="name"
            type="text"
            value={inputs.name}
            placeholder="Enter name of product"
            onChange={(e) => handleInputChange(e)}
          />
        </label>
        <label htmlFor="price">
          Price
          <input
            id="price"
            name="price"
            type="number"
            value={inputs.price}
            placeholder="Enter price of product"
            onChange={(e) => handleInputChange(e)}
          />
        </label>
        <label htmlFor="description">
          Description
          <textarea
            id="description"
            name="description"
            value={inputs.description}
            placeholder="Enter description of product"
            onChange={(e) => handleInputChange(e)}
          />
        </label>
        <button type="submit">+ Add product</button>
        <button type="submit" onClick={clearForm}>
          Clear form
        </button>
        <button type="submit" onClick={resetForm}>
          Reset form
        </button>
      </fieldset>
    </Form>
  );
}

EditForm.propTypes = {
  product: PropTypes.object,
};
