import { useMutation } from '@apollo/client';
import gql from 'graphql-tag';
import Router from 'next/router';
import useForm from '../lib/useForm';
import Form from './styles/Form';
import { ALL_PRODUCTS_QUERY } from './Products/ProductsListing';

const CREATE_PRODUCT_MUTATION = gql`
  mutation CREATE_PRODUCT_MUTATION(
    $name: String!
    $description: String!
    $status: String
    $price: Int!
    $image: Upload
  ) {
    createProduct(
      data: {
        name: $name
        description: $description
        status: $status
        price: $price
        photo: { create: { image: $image, altText: $name } }
      }
    ) {
      id
      name
      description
      status
      price
    }
  }
`;

export default function SellForm() {
  const { inputs, handleInputChange, clearForm, resetForm } = useForm({
    name: '',
    description: '',
    price: 0,
    image: '',
  });

  const [createProduct, { data, loading, error }] = useMutation(
    CREATE_PRODUCT_MUTATION,
    {
      variables: inputs,
      refetchQueries: [{ query: ALL_PRODUCTS_QUERY }],
    }
  );

  console.log(data);

  return (
    <Form
      onSubmit={async (e) => {
        // handleSubmit(e);
        e.preventDefault();
        await createProduct();
        clearForm();
        // Router.push({
        //   pathname: `/products/${data.createProduct.id}`,
        // });
      }}
    >
      <fieldset aria-busy={loading} disabled={loading}>
        <label htmlFor="image">
          Image
          <input
            required
            id="image"
            name="image"
            type="file"
            onChange={(e) => handleInputChange(e)}
          />
        </label>
        <label htmlFor="name">
          Name
          <input
            id="name"
            name="name"
            type="text"
            value={inputs.name}
            placeholder="Enter name of product"
            onChange={(e) => handleInputChange(e)}
          />
        </label>
        <label htmlFor="price">
          Price
          <input
            id="price"
            name="price"
            type="number"
            value={inputs.price}
            placeholder="Enter price of product"
            onChange={(e) => handleInputChange(e)}
          />
        </label>
        <label htmlFor="description">
          Description
          <textarea
            id="description"
            name="description"
            value={inputs.description}
            placeholder="Enter description of product"
            onChange={(e) => handleInputChange(e)}
          />
        </label>
        <button type="submit">+ Add product</button>
        <button type="submit" onClick={clearForm}>
          Clear form
        </button>
        <button type="submit" onClick={resetForm}>
          Reset form
        </button>
      </fieldset>
    </Form>
  );
}
