import useForm from '../lib/useForm';
import Form from './styles/Form';

export default function SellForm() {
  const { inputs, handleInputChange, clearForm, resetForm, handleSubmit } =
    useForm({
      name: '',
      description: '',
      price: 0,
    });

  return (
    <Form onSubmit={(e) => handleSubmit(e, inputs)}>
      <label htmlFor="name">
        Name
        <input
          id="name"
          name="name"
          type="text"
          value={inputs.name}
          placeholder="Enter name of product"
          onChange={(e) => handleInputChange(e)}
        />
      </label>
      <label htmlFor="price">
        Price
        <input
          id="price"
          name="price"
          type="number"
          value={inputs.price}
          placeholder="Enter price of product"
          onChange={(e) => handleInputChange(e)}
        />
      </label>
      <label htmlFor="description">
        Description
        <textarea
          id="description"
          name="description"
          value={inputs.description}
          placeholder="Enter description of product"
          onChange={(e) => handleInputChange(e)}
        />
      </label>
      <button type="submit" onClick={clearForm}>
        Clear form
      </button>
      <button type="submit" onClick={resetForm}>
        Reset form
      </button>
    </Form>
  );
}
